client/src/pages/services.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Plus, Edit, Trash2, Save, X } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import type { Service, InsertService } from "@shared/schema";

export default function Services() {
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editForm, setEditForm] = useState({ name: "", color: "" });
  const [newService, setNewService] = useState({ name: "", color: "#3b82f6" });
  const [showAddForm, setShowAddForm] = useState(false);
  const { toast } = useToast();

  const { data: services = [], isLoading } = useQuery<Service[]>({
    queryKey: ["/api/services"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: InsertService) => {
      const response = await apiRequest("POST", "/api/services", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/services"] });
      setNewService({ name: "", color: "#3b82f6" });
      setShowAddForm(false);
      toast({
        title: "Success",
        description: "Service created successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<Service> }) => {
      const response = await apiRequest("PATCH", `/api/services/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/services"] });
      setEditingId(null);
      toast({
        title: "Success",
        description: "Service updated successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/services/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/services"] });
      toast({
        title: "Success",
        description: "Service deleted successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleEdit = (service: Service) => {
    setEditingId(service.id);
    setEditForm({ name: service.name, color: service.color });
  };

  const handleSave = () => {
    if (editingId) {
      updateMutation.mutate({ id: editingId, data: editForm });
    }
  };

  const handleCancel = () => {
    setEditingId(null);
    setEditForm({ name: "", color: "" });
  };

  const handleCreate = () => {
    if (newService.name.trim()) {
      createMutation.mutate({
        name: newService.name,
        color: newService.color,
        icon: "circle" // Default icon
      });
    }
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="animate-pulse space-y-4">
          <div className="h-8 bg-gray-200 rounded w-1/4"></div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {[1, 2, 3].map((i) => (
              <div key={i} className="h-32 bg-gray-200 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Services</h1>
          <p className="text-gray-600">Manage available services for payment codes</p>
        </div>
        <Button 
          onClick={() => setShowAddForm(true)}
          className="flex items-center gap-2"
        >
          <Plus className="w-4 h-4" />
          Add Service
        </Button>
      </div>

      {showAddForm && (
        <Card className="mb-6">
          <CardHeader>
            <CardTitle>Add New Service</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex gap-4 items-end">
              <div className="flex-1">
                <label className="block text-sm font-medium mb-2">Service Name</label>
                <Input
                  value={newService.name}
                  onChange={(e) => setNewService({ ...newService, name: e.target.value })}
                  placeholder="e.g., Orange, Inwi, Maroc Telecom"
                />
              </div>
              <div>
                <label className="block text-sm font-medium mb-2">Color</label>
                <input
                  type="color"
                  value={newService.color}
                  onChange={(e) => setNewService({ ...newService, color: e.target.value })}
                  className="w-16 h-10 border border-gray-300 rounded cursor-pointer"
                />
              </div>
              <Button 
                onClick={handleCreate} 
                disabled={createMutation.isPending}
                className="flex items-center gap-2"
              >
                <Save className="w-4 h-4" />
                Save
              </Button>
              <Button 
                variant="outline" 
                onClick={() => setShowAddForm(false)}
                className="flex items-center gap-2"
              >
                <X className="w-4 h-4" />
                Cancel
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {services.map((service) => (
          <Card key={service.id} className="relative">
            <CardHeader>
              {editingId === service.id ? (
                <div className="space-y-3">
                  <Input
                    value={editForm.name}
                    onChange={(e) => setEditForm({ ...editForm, name: e.target.value })}
                    placeholder="Service name"
                  />
                  <input
                    type="color"
                    value={editForm.color}
                    onChange={(e) => setEditForm({ ...editForm, color: e.target.value })}
                    className="w-16 h-8 border border-gray-300 rounded cursor-pointer"
                  />
                  <div className="flex gap-2">
                    <Button 
                      size="sm" 
                      onClick={handleSave}
                      disabled={updateMutation.isPending}
                      className="flex items-center gap-1"
                    >
                      <Save className="w-3 h-3" />
                      Save
                    </Button>
                    <Button 
                      size="sm" 
                      variant="outline" 
                      onClick={handleCancel}
                      className="flex items-center gap-1"
                    >
                      <X className="w-3 h-3" />
                      Cancel
                    </Button>
                  </div>
                </div>
              ) : (
                <>
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-lg">{service.name}</CardTitle>
                    <div className="flex gap-1">
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => handleEdit(service)}
                        className="h-8 w-8 p-0"
                      >
                        <Edit className="w-3 h-3" />
                      </Button>
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => deleteMutation.mutate(service.id)}
                        disabled={deleteMutation.isPending}
                        className="h-8 w-8 p-0 text-red-500 hover:text-red-700"
                      >
                        <Trash2 className="w-3 h-3" />
                      </Button>
                    </div>
                  </div>
                  <CardDescription>
                    <Badge 
                      style={{ backgroundColor: service.color, color: '#fff' }}
                      className="text-xs"
                    >
                      {service.name}
                    </Badge>
                  </CardDescription>
                </>
              )}
            </CardHeader>
          </Card>
        ))}
      </div>

      {services.length === 0 && !showAddForm && (
        <div className="text-center py-12">
          <h3 className="text-lg font-medium text-gray-900 mb-2">No services yet</h3>
          <p className="text-gray-600 mb-4">Get started by adding your first service</p>
          <Button onClick={() => setShowAddForm(true)}>
            <Plus className="w-4 h-4 mr-2" />
            Add Service
          </Button>
        </div>
      )}
    </div>
  );
}

client/src/pages/settings.tsx.content:
import { useState, useEffect } from "react";
import { Save, Database, Bell, Shield, Palette, Globe, CheckCircle, XCircle, Loader2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { useTheme } from "@/components/theme-provider";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

export default function Settings() {
  const [settings, setSettings] = useState({
    // General Settings
    companyName: "Customer Manager",
    companyPhone: "",
    companyAddress: "",

    // Firebase Configuration
    firebaseApiKey: import.meta.env.VITE_FIREBASE_API_KEY || "",
    firebaseProjectId: import.meta.env.VITE_FIREBASE_PROJECT_ID || "",
    firebaseAppId: import.meta.env.VITE_FIREBASE_APP_ID || "",

    // Notification Settings
    emailNotifications: true,
    smsNotifications: false,
    pushNotifications: true,

    // UI Settings
    theme: "light",
    language: "en",
    dateFormat: "dd/mm/yyyy",
    currency: "MAD",

    // Security Settings
    autoLogout: true,
    sessionTimeout: 60,
    twoFactorAuth: false,

    // Backup Settings
    autoBackup: true,
    backupFrequency: "daily",
    retentionDays: 30,
  });

  const [connectionStatus, setConnectionStatus] = useState<"idle" | "testing" | "success" | "error">("idle");
  const [connectionMessage, setConnectionMessage] = useState("");

  const { toast } = useToast();
  const { theme, setTheme } = useTheme();
  const queryClient = useQueryClient();

  // Load settings from Firebase
  const { data: savedSettings } = useQuery({
    queryKey: ["/api/settings"],
  });

  // Save settings mutation
  const saveSettingsMutation = useMutation({
    mutationFn: (data: any) => 
      fetch("/api/settings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      }).then(res => res.json()),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/settings"] });
      toast({
        title: "Settings saved",
        description: "Your preferences have been updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to save settings",
        variant: "destructive",
      });
    },
  });

  // Test Firebase connection mutation
  const testConnectionMutation = useMutation({
    mutationFn: () => 
      fetch("/api/settings/test-connection", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
      }).then(res => res.json()),
    onSuccess: (data: any) => {
      setConnectionStatus(data.success ? "success" : "error");
      setConnectionMessage(data.message);
    },
    onError: () => {
      setConnectionStatus("error");
      setConnectionMessage("Connection test failed");
    },
  });

  // Load saved settings when data is available
  useEffect(() => {
    if (savedSettings) {
      setSettings(prev => ({ ...prev, ...savedSettings }));
    }
  }, [savedSettings]);

  const handleSave = () => {
    saveSettingsMutation.mutate(settings);
  };

  const handleTestConnection = () => {
    setConnectionStatus("testing");
    setConnectionMessage("");
    testConnectionMutation.mutate();
  };

  const handleReset = () => {
    // Reset to defaults and clear from database
    const defaultSettings = {
      companyName: "Customer Manager",
      companyPhone: "",
      companyAddress: "",
      emailNotifications: true,
      smsNotifications: false,
      pushNotifications: true,
      theme: "light",
      language: "en",
      dateFormat: "dd/mm/yyyy",
      currency: "MAD",
      autoLogout: true,
      sessionTimeout: 60,
      twoFactorAuth: false,
      autoBackup: true,
      backupFrequency: "daily",
      retentionDays: 30,
    };
    setSettings(prev => ({ ...prev, ...defaultSettings }));
    saveSettingsMutation.mutate(defaultSettings);
  };

  const updateSetting = (key: string, value: any) => {
    setSettings(prev => ({ ...prev, [key]: value }));
  };

  const getConnectionStatusIcon = () => {
    switch (connectionStatus) {
      case "testing":
        return <Loader2 className="h-4 w-4 animate-spin text-blue-600" />;
      case "success":
        return <CheckCircle className="h-4 w-4 text-green-600" />;
      case "error":
        return <XCircle className="h-4 w-4 text-red-600" />;
      default:
        return <Database className="h-4 w-4 text-gray-400" />;
    }
  };

  const getConnectionStatusBadge = () => {
    switch (connectionStatus) {
      case "testing":
        return <Badge variant="secondary">Testing...</Badge>;
      case "success":
        return <Badge variant="default" className="bg-green-100 text-green-700">Connected</Badge>;
      case "error":
        return <Badge variant="destructive">Connection Failed</Badge>;
      default:
        return <Badge variant="outline">Not Tested</Badge>;
    }
  };

  return (
    <div className="p-6 bg-white dark:bg-gray-900 min-h-screen">
      <div className="mb-6">
        <h1 className="text-2xl font-bold text-gray-900 dark:text-gray-100">Settings</h1>
        <p className="text-gray-600 dark:text-gray-400">Configure your application preferences</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">

        {/* General Settings */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Globe className="w-5 h-5" />
              General Settings
            </CardTitle>
            <CardDescription>
              Basic company and application information
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="company-name">Company Name</Label>
              <Input
                id="company-name"
                value={settings.companyName}
                onChange={(e) => updateSetting("companyName", e.target.value)}
                placeholder="Your company name"
              />
            </div>
            <div>
              <Label htmlFor="company-phone">Company Phone</Label>
              <Input
                id="company-phone"
                value={settings.companyPhone}
                onChange={(e) => updateSetting("companyPhone", e.target.value)}
                placeholder="Company phone number"
              />
            </div>
            <div>
              <Label htmlFor="company-address">Company Address</Label>
              <Input
                id="company-address"
                value={settings.companyAddress}
                onChange={(e) => updateSetting("companyAddress", e.target.value)}
                placeholder="Company address"
              />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="language">Language</Label>
                <Select value={settings.language} onValueChange={(value) => updateSetting("language", value)}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="en">English</SelectItem>
                    <SelectItem value="fr">FranÃ§ais</SelectItem>
                    <SelectItem value="ar">Ø§Ù„Ø¹Ø±Ø¨ÙŠØ©</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="currency">Currency</Label>
                <Select value={settings.currency} onValueChange={(value) => updateSetting("currency", value)}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="MAD">MAD (Dirham)</SelectItem>
                    <SelectItem value="EUR">EUR (Euro)</SelectItem>
                    <SelectItem value="USD">USD (Dollar)</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Firebase Configuration */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Database className="w-5 h-5" />
              Firebase Database
            </CardTitle>
            <CardDescription>
              Firebase project configuration and connection status
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
              <div className="flex items-center gap-2">
                {getConnectionStatusIcon()}
                <span className="text-sm font-medium">Connection Status</span>
              </div>
              {getConnectionStatusBadge()}
            </div>

            {connectionMessage && (
              <div className={`p-3 rounded-lg text-sm ${
                connectionStatus === "success" 
                  ? "bg-green-50 text-green-700 border border-green-200" 
                  : connectionStatus === "error"
                  ? "bg-red-50 text-red-700 border border-red-200"
                  : "bg-blue-50 text-blue-700 border border-blue-200"
              }`}>
                {connectionMessage}
              </div>
            )}

            <div>
              <Label htmlFor="firebase-api-key">API Key</Label>
              <Input
                id="firebase-api-key"
                type="password"
                value={settings.firebaseApiKey}
                onChange={(e) => updateSetting("firebaseApiKey", e.target.value)}
                placeholder="Firebase API Key"
              />
              <p className="text-xs text-gray-500 mt-1">
                Environment: {import.meta.env.VITE_FIREBASE_API_KEY ? "Configured" : "Not Set"}
              </p>
            </div>
            <div>
              <Label htmlFor="firebase-project-id">Project ID</Label>
              <Input
                id="firebase-project-id"
                value={settings.firebaseProjectId}
                onChange={(e) => updateSetting("firebaseProjectId", e.target.value)}
                placeholder="Firebase Project ID"
              />
              <p className="text-xs text-gray-500 mt-1">
                Environment: {import.meta.env.VITE_FIREBASE_PROJECT_ID ? "Configured" : "Not Set"}
              </p>
            </div>
            <div>
              <Label htmlFor="firebase-app-id">App ID</Label>
              <Input
                id="firebase-app-id"
                value={settings.firebaseAppId}
                onChange={(e) => updateSetting("firebaseAppId", e.target.value)}
                placeholder="Firebase App ID"
              />
              <p className="text-xs text-gray-500 mt-1">
                Environment: {import.meta.env.VITE_FIREBASE_APP_ID ? "Configured" : "Not Set"}
              </p>
            </div>

            <Button 
              onClick={handleTestConnection}
              disabled={testConnectionMutation.isPending || connectionStatus === "testing"}
              className="w-full"
              variant="outline"
            >
              {testConnectionMutation.isPending ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Testing Connection...
                </>
              ) : (
                <>
                  <Database className="w-4 h-4 mr-2" />
                  Test Firebase Connection
                </>
              )}
            </Button>
          </CardContent>
        </Card>

        {/* UI & Display Settings */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Palette className="w-5 h-5" />
              UI & Display
            </CardTitle>
            <CardDescription>
              Customize the appearance and behavior
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="theme">Theme</Label>
                <Select value={theme} onValueChange={(value) => setTheme(value as "light" | "dark" | "system")}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="light">Light</SelectItem>
                    <SelectItem value="dark">Dark</SelectItem>
                    <SelectItem value="system">System</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="date-format">Date Format</Label>
                <Select value={settings.dateFormat} onValueChange={(value) => updateSetting("dateFormat", value)}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="dd/mm/yyyy">DD/MM/YYYY</SelectItem>
                    <SelectItem value="mm/dd/yyyy">MM/DD/YYYY</SelectItem>
                    <SelectItem value="yyyy-mm-dd">YYYY-MM-DD</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Notifications */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
